Collection.create!([
  {title: "Video History of the Castro Era", description: "<p>These are uncut interviews conducted in English and Spanish by Estela Bravo for her 2001 film <em>Fidel: The Untold Story</em>. Most of the interviews were conducted in 1996, with a few from 1997 and 1998.</p>\r\n\r\n<p>For more information about these interviews, please see the <a href=\"/finding_aids\">finding aid</a>.</p>\r\n", slug: "video-history-of-the-castro-era", image_file_name: "luis_korda_fidel_and_cienfuegos.jpg", image_content_type: "image/jpeg", image_file_size: 53695, image_updated_at: "2015-02-13 20:53:11"}
])
